(2, 'Confession BG5.1', '', 2, '1', '', 'Confession of Sins', '1', '', '<div>\r\n	Almighty and most merciful Father,</div>\r\n<div>\r\n	we have erred and strayed from your ways like lost sheep.</div>\r\n<div>\r\n	We have followed too much the devices and desires of our own hearts.</div>\r\n<div>\r\n	We have offended against your holy laws.</div>\r\n<div>\r\n	We have left undone what we ought to have done,</div>\r\n<div>\r\n	and we have done what we ought not to have done,</div>\r\n<div>\r\n	and there is no health in us.</div>\r\n<div>\r\n	Yet, good Lord, have mercy on us:</div>\r\n<div>\r\n	restore those who are penitent,</div>\r\n<div>\r\n	according to your promises declared to us in Jesus Christ our Lord.</div>\r\n<div>\r\n	And grant, merciful Father, for his sake,</div>\r\n<div>\r\n	That we may live a godly, righteous, and obedient life,</div>\r\n<div>\r\n	to the glory of your holy name. Amen.&nbsp;</div>\r\n', '', 0),
(2, 'Confession BG5.2', '', 3, '1', '', 'Confession of Sins', '1', '', '<div>\r\n	Most merciful God,</div>\r\n<div>\r\n	we humbly admit that we need your help.</div>\r\n<div>\r\n	We confess that we have wandered from your way:</div>\r\n<div>\r\n	we have done wrong, and we have failed to do what is right.</div>\r\n<div>\r\n	You alone can save us.</div>\r\n<div>\r\n	Have mercy on us:</div>\r\n<div>\r\n	wipe out our sins and teach us to forgive others.</div>\r\n<div>\r\n	Bring forth in us the fruit of the Spirit</div>\r\n<div>\r\n	that we may live as disciples of Christ.</div>\r\n<div>\r\n	This we ask in the name of Jesus our Saviour. Amen.&nbsp;</div>\r\n', '', 0),
(2, 'Confession BG5.3', '', 3, '1', '', 'Confession of Sins', '1', '', '<div>\r\n	Almighty God, Father of our Lord Jesus Christ,</div>\r\n<div>\r\n	maker of all things, judge of all people,</div>\r\n<div>\r\n	we acknowledge with shame the sins we have committed,</div>\r\n<div>\r\n	by thought, word and deed, against your divine majesty,</div>\r\n<div>\r\n	provoking most justly your wrath and indignation against us.</div>\r\n<div>\r\n	We earnestly repent, and are heartily sorry for all our misdoings.</div>\r\n<div>\r\n	Have mercy on us, most merciful Father.</div>\r\n<div>\r\n	For your Son our Lord Jesus Christ&#39;s sake</div>\r\n<div>\r\n	forgive us all that is past,</div>\r\n<div>\r\n	and grant that from this time forward</div>\r\n<div>\r\n	we may serve and please you in newness of life,</div>\r\n<div>\r\n	to the honour and glory of your name,</div>\r\n<div>\r\n	through Jesus Christ our Lord. Amen.</div>\r\n', '', 0),
(2, 'Confession BG5.4', '', 2, '1', '', 'Confession of Sins', '1', '', '<div>\r\n	Merciful God,</div>\r\n<div>\r\n	our maker and our judge,</div>\r\n<div>\r\n	we have sinned against you in thought, word, and deed:</div>\r\n<div>\r\n	we have not loved you with our whole heart,</div>\r\n<div>\r\n	we have not loved our neighbours as ourselves:</div>\r\n<div>\r\n	we repent, and are sorry for all our sins.</div>\r\n<div>\r\n	Father, forgive us.</div>\r\n<div>\r\n	Strengthen us to love and obey you in newness of life;</div>\r\n<div>\r\n	through Jesus Christ our Lord. Amen.</div>\r\n', '', 0),
(2, 'Confession BG5.5', '', 2, '1', '', 'Confession of Sins', '1', '', '<div>\r\n	Heavenly Father,</div>\r\n<div>\r\n	we praise you for adopting us as your children</div>\r\n<div>\r\n	and making us heirs of eternal life.</div>\r\n<div>\r\n	In your mercy you have washed us from our sins</div>\r\n<div>\r\n	and made us clean in your sight.</div>\r\n<div>\r\n	Yet we still fail to love you as we should</div>\r\n<div>\r\n	and serve you as we ought.</div>\r\n<div>\r\n	Forgive us our sins and renew us by your grace,</div>\r\n<div>\r\n	that we may continue to grow as members of Christ,</div>\r\n<div>\r\n	in whom alone is our salvation. Amen.</div>\r\n', '', 0),
