(3, 'Apostles'' Creed', 'Normal version', 2, '1', '', '', '1', '', '<div>\r\n	I believe in God, the Father Almighty,</div>\r\n<div>\r\n	creator of heaven and earth.</div>\r\n<div>\r\n	I believe in Jesus Christ,</div>\r\n<div>\r\n	God&#39;s only Son, our Lord,</div>\r\n<div>\r\n	who was conceived by the Holy Spirit,</div>\r\n<div>\r\n	born of the Virgin Mary,</div>\r\n<div>\r\n	suffered under Pontius Pilate,</div>\r\n<div>\r\n	was crucified, died, and was buried;</div>\r\n<div>\r\n	he descended to the dead.</div>\r\n<div>\r\n	On the third day he rose from the dead;</div>\r\n<div>\r\n	he ascended into heaven,</div>\r\n<div>\r\n	and is seated at the right hand of the Father;</div>\r\n<div>\r\n	from there he will come to judge</div>\r\n<div>\r\n	the living and the dead.</div>\r\n<div>\r\n	I believe in the Holy Spirit,</div>\r\n<div>\r\n	the holy universal Church,</div>\r\n<div>\r\n	the communion of saints,</div>\r\n<div>\r\n	the forgiveness of sins,</div>\r\n<div>\r\n	the resurrection of the body,</div>\r\n<div>\r\n	and the life everlasting. Amen.&nbsp;</div>\r\n', '', 0),
(3, 'Nicene Creed', '', 2, '1', '', '', '1', '', '<div>\r\n	We believe in one God,</div>\r\n<div>\r\n	the Father, the Almighty,</div>\r\n<div>\r\n	maker of heaven and earth</div>\r\n<div>\r\n	of all that is, seen and unseen.</div>\r\n<div>\r\n	We believe in one Lord, Jesus Christ,</div>\r\n<div>\r\n	the only Son of God,</div>\r\n<div>\r\n	eternally begotten of the Father,</div>\r\n<div>\r\n	God from God, Light from Light,</div>\r\n<div>\r\n	true God from true God,</div>\r\n<div>\r\n	begotten not made,</div>\r\n<div>\r\n	of one being with the Father;</div>\r\n<div>\r\n	through him all things were made.</div>\r\n<div>\r\n	For us and for our salvation</div>\r\n<div>\r\n	he came down from heaven,</div>\r\n<div>\r\n	was incarnate of the Holy Spirit and the Virgin Mary</div>\r\n<div>\r\n	and became truly human.</div>\r\n<div>\r\n	For our sake he was crucified under Pontius Pilate;</div>\r\n<div>\r\n	he suffered death and was buried.</div>\r\n<div>\r\n	On the third day he rose again</div>\r\n<div>\r\n	in accordance with the Scriptures;</div>\r\n<div>\r\n	he ascended into heaven</div>\r\n<div>\r\n	and is seated at the right hand of the Father.</div>\r\n<div>\r\n	He will come again in glory to judge</div>\r\n<div>\r\n	the living and the dead</div>\r\n<div>\r\n	and his kingdom will have no end.</div>\r\n<div>\r\n	We believe in the Holy Spirit,</div>\r\n<div>\r\n	the Lord, the giver of life,</div>\r\n<div>\r\n	who proceeds from the Father and the Son,</div>\r\n<div>\r\n	who with the Father and the Son</div>\r\n<div>\r\n	is worshipped and glorified,</div>\r\n<div>\r\n	who has spoken through the prophets.</div>\r\n<div>\r\n	We believe in one holy universal and apostolic Church.</div>\r\n<div>\r\n	We acknowledge one baptism for the forgiveness of sins.</div>\r\n<div>\r\n	We look for the resurrection of the dead,</div>\r\n<div>\r\n	and the life of the world to come. Amen.</div>\r\n', '', 0),
(3, 'Apostles'' Creed', 'Responsive version', 2, '1', '', '', '1', '', '<div>\r\n	Do you believe and trust in God the Father?</div>\r\n<div>\r\n	<strong>We believe in God, the Father Almighty,</strong></div>\r\n<div>\r\n	<strong>creator of heaven and earth.</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Do you believe and trust in God the Son?</div>\r\n<div>\r\n	<strong>We believe in Jesus Christ,</strong></div>\r\n<div>\r\n	<strong>God&#39;s only Son, our Lord,</strong></div>\r\n<div>\r\n	<strong>who was conceived by the Holy Spirit,</strong></div>\r\n<div>\r\n	<strong>born of the Virgin Mary,</strong></div>\r\n<div>\r\n	<strong>suffered under Pontius Pilate,</strong></div>\r\n<div>\r\n	<strong>was crucified, died and was buried;</strong></div>\r\n<div>\r\n	<strong>he descended to the dead.</strong></div>\r\n<div>\r\n	<strong>On the third day he rose from the dead;</strong></div>\r\n<div>\r\n	<strong>he ascended into heaven.</strong></div>\r\n<div>\r\n	<strong>and is seated at the right hand of the Father;</strong></div>\r\n<div>\r\n	<strong>from there he will come again to</strong></div>\r\n<div>\r\n	<strong>judge the living and the dead.</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Do you believe and trust in the Holy Spirit?</div>\r\n<div>\r\n	<strong>We believe in the Holy Spirit,</strong></div>\r\n<div>\r\n	<strong>the holy, universal Church,</strong></div>\r\n<div>\r\n	<strong>the fellowship of the saints,</strong></div>\r\n<div>\r\n	<strong>the forgiveness of sins,</strong></div>\r\n<div>\r\n	<strong>the resurrection of the body, and life eternal. Amen.</strong></div>\r\n', '', 0),
