(2, 'Thanksgiving BG11.1', 'We give you humble and hearty thanks...', 2, '1', '', 'Prayer of Thanksgiving', '1', '', '<div>\r\n	Almighty God and merciful Father,</div>\r\n<div>\r\n	we give you humble and hearty thanks</div>\r\n<div>\r\n	for all your goodness and loving kindness</div>\r\n<div>\r\n	to us and to all people.</div>\r\n<div>\r\n	We praise you for creating and sustaining us</div>\r\n<div>\r\n	and for all the blessings of this life;</div>\r\n<div>\r\n	but above all for your amazing love</div>\r\n<div>\r\n	in redeeming the world by our Lord Jesus Christ,</div>\r\n<div>\r\n	giving us grace and the hope of glory.</div>\r\n<div>\r\n	Give us such a sense of all you goodness,</div>\r\n<div>\r\n	that we may be truly thankful, and may praise you</div>\r\n<div>\r\n	not only with our lips, but in our lives,</div>\r\n<div>\r\n	by serving you in holy and righteous ways;</div>\r\n<div>\r\n	through Jesus Christ our Lord,</div>\r\n<div>\r\n	to whom with you and the Holy Spirit,</div>\r\n<div>\r\n	be all honour and glory, now and for ever. Amen.</div>\r\n', '', 0),
(2, 'Thanksgiving BG11.2', 'We humbly thank you for all your gifts...', 1, '1', '', 'Prayer of Thanksgiving', '1', '', '<div>\r\n	Gracious God, our heavenly Father,</div>\r\n<div>\r\n	we humbly thank you</div>\r\n<div>\r\n	for all your gifts so freely given:</div>\r\n<div>\r\n	for life and health and safety,</div>\r\n<div>\r\n	for work and rest and friendship,</div>\r\n<div>\r\n	and for the wonder of creation.</div>\r\n<div>\r\n	We thank you for preserving throughout history</div>\r\n<div>\r\n	a people for yourself.</div>\r\n<div>\r\n	Above all, we praise you for our Saviour Jesus Christ,</div>\r\n<div>\r\n	for his death and resurrection,</div>\r\n<div>\r\n	for your life-giving Spirit,</div>\r\n<div>\r\n	and the hope of sharing in your glory.</div>\r\n<div>\r\n	Fill our hearts with all joy and peace in believing;</div>\r\n<div>\r\n	through Jesus Christ our Lord. Amen.</div>\r\n', '', 0),
(2, 'Thanksgiving BG11.3', 'We praise you for the beauty of the world around us...', 1, '1', '', 'Prayer of Thanksgiving', '1', '', '<div>\r\n	Almighty God, creator and redeemer,</div>\r\n<div>\r\n	we praise you for the beauty of the world around us,</div>\r\n<div>\r\n	and for every gift we enjoy.</div>\r\n<div>\r\n	Thank you for creating us</div>\r\n<div>\r\n	to know you, to love you and to obey you.</div>\r\n<div>\r\n	Most of all we praise you</div>\r\n<div>\r\n	for your amazing love</div>\r\n<div>\r\n	in sending your Son,</div>\r\n<div>\r\n	to restore your world,</div>\r\n<div>\r\n	by dying for us</div>\r\n<div>\r\n	and rising to give us new life.</div>\r\n<div>\r\n	Accept our praise and thanksgiving,</div>\r\n<div>\r\n	through Jesus Christ our Lord. Amen.</div>\r\n', '', 0),
(2, 'Thanksgiving BG11.4', 'You made us in your image to enjoy and govern them...', 1, '1', '', 'Prayer of Thanksgiving', '1', '', '<div>\r\n	Lord, you made all things,</div>\r\n<div>\r\n	and you made us in your image</div>\r\n<div>\r\n	to enjoy and govern them.</div>\r\n<div>\r\n	We thank you</div>\r\n<div>\r\n	for your work of creation,</div>\r\n<div>\r\n	for the beauty we enjoy,</div>\r\n<div>\r\n	for human skill and artistry.</div>\r\n<div>\r\n	We thank you</div>\r\n<div>\r\n	for community, friendship and love,</div>\r\n<div>\r\n	for family and marriage,</div>\r\n<div>\r\n	for the privilege of parenthood.</div>\r\n<div>\r\n	Above all we thank you</div>\r\n<div>\r\n	for Jesus, your Son,</div>\r\n<div>\r\n	through whom you created all things,</div>\r\n<div>\r\n	and by whom you sustain them.</div>\r\n<div>\r\n	We thank you</div>\r\n<div>\r\n	that he became one of us and died</div>\r\n<div>\r\n	to reconcile all creation to you,</div>\r\n<div>\r\n	in heaven and on earth,</div>\r\n<div>\r\n	and to call us into peace with you. Amen.</div>\r\n', '', 0),
(2, 'Lord''s Prayer', '', 2, '1', '', 'The Lord''s Prayer', '1', '', '<div>\r\n	Our Father in heaven,</div>\r\n<div>\r\n	hallowed be your name,</div>\r\n<div>\r\n	your kingdom come,</div>\r\n<div>\r\n	your will be done, on earth as in heaven.</div>\r\n<div>\r\n	Give us today our daily bread.</div>\r\n<div>\r\n	Forgive us our sins</div>\r\n<div>\r\n	as we forgive those who sin against us</div>\r\n<div>\r\n	Lead us not into temptation,</div>\r\n<div>\r\n	but deliver us from evil.</div>\r\n<div>\r\n	For the kingdom, the power, and the glory are yours</div>\r\n<div>\r\n	now and forever. Amen.</div>\r\n', '', 0),
